import Card from '../UI/Card';
import classes from './ProductItem.module.css';
import { useDispatch } from 'react-redux';
import { shoppingCartActions } from '../../store/ShoppingCart';
import {useRef} from 'react';

const ProductItem = (props) => {
  const titleRef = useRef("");
  const priceRef = useRef(0);
  const descriptionRef = useRef("");
  const dispatch = useDispatch();
  const addToCartHandler = () => {
    console.log('clicked');
    dispatch(shoppingCartActions.addItem({
      title: titleRef.current.value,
      price: +priceRef.current.value,
      description: descriptionRef.current.value,
      quantity: 1
    }));
  }
  return (
    <li className={classes.item}>
      <Card>
        <header className={classes.header}>
          <div className={classes.inputGroup}>
            <label className={classes.label}>Title:</label>
            <input type="text" ref={titleRef} className={classes.input} />
          </div>
          <div className={classes.inputGroup}>
            <label className={classes.label}>Price:$</label>
            <input type="number" ref={priceRef} className={`${classes.input} ${classes.price}`} />
          </div>
        </header>
        <div className={classes.inputGroup}>
          <label className={classes.label}>Description:</label>
          <textarea ref={descriptionRef} className={classes.textarea}></textarea>
        </div>
        <div className={classes.actions}>
          <button className={classes.button} onClick={addToCartHandler}>Add to Cart</button>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
